import { each, every, find, findLast, includes, isArray, isEmpty, isFunction, last, map, reject, sortedIndexBy } from 'lodash';
import EventEmitter from 'events';
import { fn as isGeneratorFunction } from 'is-generator';
import Promise from 'bluebird';

import SpliceTextMutation from './mutation.splice';
import TextElement from './element';
import ParserBuilder from './parser-builder';

/**
 * A plain text document that can be mutated (or modified).
 *
 * To mutate a document, you must first add parsers which will identify elements in the text, using {@link TextDocument#addParser} or {@link TextDocument#buildParser}.
 *
 * Parsers must be generator functions which parse the document and yield the text elements they identify.
 * These elements must be subclasses of {@link TextElement} and must be yielded in the order they are found in the document.
 * If the element needs to be mutated, its subclass must know how to modify its own text or the document to obtain the desired end result.
 * See {@link ParserBuilder} for an example of how to create a parser.
 *
 * When you have added all the parsers you need, call {@link TextDocument#mutate}.
 * This will trigger the parsing, then mutate each element one by one until the end of the document has been reached.
 *
 * ## Events
 *
 * A text document is an {@link events~EventEmitter} that emits the following events:
 *
 * * `element` - Emitted after calling {@link TextDocument#mutate} while the parsing is in progress, for each new element identified by a parser.
 *   The parsed {@link TextElement} is provided to listeners.
 * * `parsed` - Emitted after calling {@link TextDocument#mutate} when parsing the document is complete.
 * * `mutate` - Emitted after calling {@link TextDocument#mutate} while the document is being mutated, once for each element that triggered a mutation.
 *   The {@link TextMutation} object describing the change is provided to listeners.
 */
export default class TextDocument extends EventEmitter {

  /**
   * Creates a text document.
   *
   * @param {string} text - The document's text.
   */
  constructor(text) {
    super();

    /**
     * The document's text.
     * @type {string}
     */
    this.text = text;

    /**
     * The document's parsers.
     * @type {Array<function>}
     */
    this.parsers = [];

    /**
     * The {@link TextElement} objects identified in the document by the parsers.
     * @type {Array<TextElement>}
     */
    this.elements = [];

    // TODO: automatically update max listeners
    this.setMaxListeners(1000);
  }

  /**
   * Adds a parser for this document.
   *
   * Parsers will be invoked when calling {@link TextDocument#mutate}.
   *
   * @param {function} parser - A generator function that yields {@link TextElement} objects in order, or a function that returns an ordered array of parsed {@link TextElement} objects.
   * @returns {TextDocument} This document.
   */
  addParser(parser) {
    if (!isFunction(parser)) {
      throw new Error('Parser must be a function or a generator function');
    } else if (!isGeneratorFunction(parser)) {
      parser = toGenerator(this, parser);
    }

    this.parsers.push(parser);

    return this;
  }

  /**
   * Returns a parser builder for this document.
   * Any argument is passed to the builder's constructor as additional arguments after this document.
   *
   * The returned builder will add a parser to this document when calling {@link ParserBuilder#add}.
   *
   * @returns {ParserBuilder} A parser builder for this document.
   */
  buildParser(...args) {
    return new ParserBuilder(this, ...args);
  }

  /**
   * Adds a text element to this document.
   *
   * The element must not overlap any other element already present in the document.
   *
   * @param {TextElement} element - The text element to add.
   * @returns {TextDocument} This document.
   */
  addElement(element) {

    if (isEmpty(this.elements) || element.start >= last(this.elements).end) {
      // If there are no elements yet, or this element is after the last element, simply add it to the list.
      this.elements.push(element);
    } else {

      // Otherwise, perform a binary search to find the correct index to insert the element
      // so that its position in the array is consistent with its position in the text.
      const elementIndex = sortedIndexBy(this.elements, element, (e) => e.start);

      // Ensure that the element does not overlap with the previous element.
      const previousElement = this.elements[elementIndex - 1];
      if (previousElement && previousElement.overlaps(element)) {
        throw new Error('Previous element ' + previousElement + ' overlaps with new element ' + element);
      }

      // Ensure that the element does not overlap with the next element.
      const nextElement = this.elements[elementIndex];
      if (nextElement && nextElement.overlaps(element)) {
        throw new Error('Next element ' + nextElement + ' overlaps with new element ' + element);
      }

      // Insert the element.
      this.elements.splice(elementIndex, 0, element);
    }

    this.emit('element', element);

    return this;
  }

  /**
   * Removes the specified element from this document.
   *
   * @param {TextElement} element - The element to remove.
   * @returns {TextDocument} This document.
   */
  removeElement(element) {

    const index = this.elements.indexOf(element);
    if (index < 0) {
      throw new Error('Element ' + element + ' is not in this document');
    }

    this.elements.splice(index, 1);

    return this;
  }

  /**
   * Mutates this document.
   *
   * This is done in two steps:
   *
   * 1. All parsers will be invoked and used to parse the entire document.
   * 2. All identified text elements will be mutated one by one in order.
   *
   * @returns {Promise<TextDocument>} A promise that will be resolved when the mutation is complete.
   */
  mutate() {

    parseTextElements(this);
    this.emit('parsed');

    let promise = Promise.resolve();
    each(this.elements.slice(), (element) => promise = promise.then(() => element.mutate()));
    promise = promise.return(this);

    return promise;
  }

  /**
   * Performs a text element mutation that appends text at the end of this document.
   *
   * @param {TextElement} element - The element that triggered the mutation.
   * @param {string} text - The text to append.
   * @returns {TextDocument} This document.
   */
  append(element, text) {
    return this.insert(element, this.text.length, text);
  }

  /**
   * Performs a text element mutation that prepends text to the beginning of this document.
   *
   * @param {TextElement} element - The element that triggered the mutation.
   * @param {string} text - The text to prepend.
   * @returns {TextDocument} This document.
   */
  prepend(element, text) {
    return this.insert(element, 0, text);
  }

  /**
   * Performs a text element mutation that inserts additional text into the document.
   *
   * @param {TextElement} element - The element that triggered the mutation.
   * @param {integer} index - The index at which to insert the text.
   * @param {string} text - The text to insert.
   * @returns {TextDocument} This document.
   */
  insert(element, index, text) {
    return performMutation(this, new SpliceTextMutation(element, index, 0, text));
  }

  /**
   * Performs a text element mutation that replaces part of the text in this document by another text.
   *
   * @param {TextElement} element - The element that triggered the mutation.
   * @param {integer} start - The start index (inclusive) of the text range to replace.
   * @param {integer} end - The end index (exclusive) of the text range to replace.
   * @param {string} text - The text to replace the selected range with.
   * @returns {TextDocument} This document.
   */
  replace(element, start, end, text) {
    return performMutation(this, new SpliceTextMutation(element, start, end - start, text));
  }

  /**
   * Performs a text element mutation that removes the element from the document.
   *
   * @param {TextElement} element - The element that triggered the mutation.
   * @param {integer} start - The start index (inclusive) of the text range to remove.
   * @param {integer} end - The end index (exclusive) of the text range to remove.
   * @returns {TextDocument} This document.
   */
  remove(element, start, end) {
    return performMutation(this, new SpliceTextMutation(element, start, end - start));
  }

  /**
   * Finds the first text element in this document that matches the specified conditions.
   *
   * @param {function(element: TextElement): boolean} predicate - A function that will be passed each element and should return true if the element matches the desired conditions.
   * @returns {TextElement|undefined} The first element matching the predicate, or `undefined`.
   */
  find(predicate) {
    return find(this.elements, predicate);
  }

  /**
   * Finds the last text element in this document that matches the specified conditions.
   *
   * @param {function(element: TextElement): boolean} predicate - A function that will be passed each element and should return true if the element matches the desired conditions.
   * @returns {TextElement|undefined} The last element matching the predicate, or `undefined`.
   */
  findLast(predicate) {
    return findLast(this.elements, predicate);
  }

  /**
   * Finds the first text element after the specified element in this document that matches the specified conditions.
   *
   * @param {TextElement} element - The element after which to search.
   * @param {function(element: TextElement): boolean} predicate - A function that will be passed each element and should return true if the element matches the desired conditions.
   * @returns {TextElement|undefined} The last element matching the predicate, or `undefined`.
   */
  findNext(element, predicate) {
    if (!includes(this.elements, element)) {
      throw new Error('Element ' + element + ' is not in document');
    }

    return this.find((e) => e.start > element.start && predicate(e));
  }

  /**
   * Finds the last text element before the specified element in this document that matches the specified conditions.
   *
   * @param {TextElement} element - The element before which to search.
   * @param {function(element: TextElement): boolean} predicate - A function that will be passed each element and should return true if the element matches the desired conditions.
   * @returns {TextElement|undefined} The last element matching the predicate, or `undefined`.
   */
  findPrevious(element, predicate) {
    if (!includes(this.elements, element)) {
      throw new Error('Element ' + element + ' is not in document');
    }

    return this.findLast((e) => e.start < element.start && predicate(e));
  }
}

// Transforms a function that returns a list to a generator function that yields the items of that list.
function toGenerator(document, parser) {
  return function*() {

    var elements = parser(document);
    if (!isArray(elements)) {
      throw new Error('Parser must be a function that returns an array of ordered TextElement objects');
    }

    while (elements.length) {
      yield elements.shift();
    }
  };
}

function performMutation(document, mutation) {
  document.text = mutation.apply(document.text);
  document.emit('mutate', mutation);
  return this;
}

function parseTextElements(document) {

  // Get the parsers' iterators (parsers are generator functions).
  let iterators = document.parsers.map((parser) => parser(document));

  // Get the first text element yielded by each parser.
  each(iterators, parseNextTextElement);

  // Reject parsers that yielded no element.
  iterators = reject(iterators, parserIsDone);

  // While there are still parsers with text elements...
  while (!every(iterators, parserIsDone)) {

    // Sort the last retrieved text elements by start index.
    iterators.sort((a, b) => a.element.start - b.element.start);

    // Add the first one to the document.
    const element = iterators[0].element;
    document.addElement(element);

    // Get the parser's next text element.
    parseNextTextElement(iterators[0]);

    // If the parser is done, remove it from the list of active parsers.
    if (parserIsDone(iterators[0])) {
      iterators.shift();
    }
  }
}

function parserIsDone(parserIterator) {
  return parserIterator.current && parserIterator.current.done;
}

function parseNextTextElement(parserIterator) {

  var previousElement = parserIterator.element;

  parserIterator.current = parserIterator.next();
  parserIterator.element = parserIterator.current.value;

  if (!parserIsDone(parserIterator) && !(parserIterator.element instanceof TextElement)) {
    throw new Error('Parsers must return TextElement objects; got ' + typeof(parserIterator.element));
  } else if (!parserIsDone(parserIterator) && previousElement && parserIterator.element.start <= previousElement.start) {
    throw new Error('Parsers must yield TextElement objects in order; got ' + parserIterator.element + ' which starts at index ' + parserIterator.element.start + ' while the previous element ' + previousElement + ' starts at index ' + previousElement.start);
  }

  return parserIterator.element;
}

import { isFunction } from 'lodash';

import TextElement from './element';

/**
 * A text element that has a configurable behavior.
 */
export default class ConfigurableTextElement extends TextElement {

  /**
   * Creates a text element with arbitrary data which can be used at mutation time.
   *
   * Also calls the `this.initializer` function attached to this element, if any.
   *
   * @param {string} document - The document in which the element was found or is to be inserted.
   * @param {integer} start - The index at which the element's text is found in the document.
   * @param {string} text - The element's text.
   * @param {object} data - Data to attach to the element.
   */
  constructor(document, start, text, data) {
    super(document, start, text);

    /**
     * Arbitrary data that will be available at mutation time.
     * @type {object}
     */
    this.data = data;

    // If an initializer function is defined, call it.
    // TODO: create a setter for the initializer and the type
    if (isFunction(this.initialize)) {
      this.initialize.call(this, data);
    }
  }

  /**
   * Calls the `this.mutator` function attached to this element, if any.
   */
  mutate() {
    // TODO: create a setter for the mutator
    if (isFunction(this.mutator)) {
      this.mutator.call(this, this.data);
    }
  }

  /**
   * Returns a string representing this element.
   */
  toStringProperties() {
    return [ 'type' ].concat(super.toStringProperties());
  }

}

import { each, pick } from 'lodash';

import TextRange from './range';
import { stringify } from './utils';

/**
 * A text element is a part of a {@link TextDocument} that can be mutated.
 *
 * It is identified by its start (inclusive) and end (exclusive) indices in the document.
 * Text elements are identified when parsing a {@link TextDocument}.
 *
 * For example, take the following Markdown document:
 *
 * ```md
 * # Documentation
 *
 * Lorem ipsum dolor sit amet.
 *
 * ## Usage
 *
 * Consectetur adipiscing elit.
 * Maecenas laoreet hendrerit felis eget vulputate.
 *
 * ### Requirements
 *
 * * Aenean quis felis lacus.
 * * Proin lobortis, mauris at congue lacinia.
 * * Erat arcu cursus diam.
 * ```
 *
 * A parser for Markdown headers could identify the following text elements:
 *
 * * `# Documentation` starting at index 0 and ending at index 15.
 * * `## Usage` starting at index 46 and ending at index 54.
 * * `### Requirements` starting at index 135 and ending at index 151.
 *
 * By using a subclass of `TextElement`, the parser might also store additional information
 * about the elements, such as the titles' contents or level in this case.
 */
export default class TextElement extends TextRange {

  /**
   * Creates a text element for the specified document.
   *
   * @param {string} document - The document in which the element was found or is to be inserted.
   * @param {integer} start - The index at which the element's text is found in the document.
   * @param {string} text - The element's text.
   */
  constructor(document, start, text) {
    // TODO: validate
    super(start, start + text.length);

    /**
     * The document in which this text element is located.
     * @type {TextDocument}
     */
    this.document = document;

    /**
     * @access private
     */
    this.documentListeners = {};

    // Keep track of document mutations.
    this.onDocument('mutate', this.onDocumentMutated);
  }

  /**
   * This element's text.
   * @type {string}
   */
  get text() {
    return this.extractFromText(this.document.text);
  }

  /**
   * Mutates this element's text.
   *
   * This is an empty implementation which should be overridden in subclasses.
   * The supplied {@link TextDocument} has many utility methods to insert, modify or remove text.
   *
   * @returns {Promise|undefined} A promise which will be resolved when the mutation is complete, or nothing if the mutation was synchronous.
   */
  mutate() {
    // nothing to do
  }

  /**
   * Appends text after this element.
   *
   * Utility method for use in {@link TextElement#mutate}.
   *
   * @param {string} text - The text to append.
   * @returns {TextElement} This element.
   *
   * @example
   * mutate() {
   *   this.append(';');
   * }
   */
  append(text) {
    this.document.insert(this, this.end, text);
    return this;
  }

  /**
   * Prepends text before this element.
   * The text is not included into the element. Rather, the element is moved forward in the document to take into account the added text.
   *
   * Utility method for use in {@link TextElement#mutate}.
   *
   * @param {string} text - The text to prepend.
   * @returns {string} This element.
   *
   * @example
   * mutate() {
   *   this.prepend('---\n');
   * }
   */
  prepend(text) {
    this.document.insert(this, this.start, text);
    this.start += text.length;
    this.end += text.length;
    return this;
  }

  /**
   * Replaces this element's text by another text.
   *
   * Utility method for use in {@link TextElement#mutate}.
   *
   * @param {string} text - The new text.
   * @returns {string} This element.
   *
   * @example
   * mutate() {
   *   this.replace('This text is better.');
   * }
   */
  replace(text) {
    this.document.replace(this, this.start, this.end, text);
    this.end = this.end + text.length - (this.end - this.start);
    return this;
  }

  /**
   * Removes this element's text from the document.
   *
   * This will also remove the element from the document's list of elements,
   * and remove all of this elements' listeners registered with {@link TextElement#onDocument}.
   *
   * Utility method for use in {@link TextElement#mutate}.
   *
   * @returns {string} This element.
   *
   * @example
   * mutate() {
   *   this.remove();
   * }
   */
  remove() {
    this.document.remove(this, this.start, this.end);
    this.document.removeElement(this);
    this.removeDocumentListeners();
    return this;
  }

  /**
   * Registers a listener on a document event.
   *
   * Listeners registered with this method can later be removed with {@link TextElement#removeDocumentListeners}.
   *
   * @param {string} event - The name of the event to listen to.
   * @param {function} listener - The callback function.
   * @param {any} [context] - The context to which the callback function will be bound (`this` by default).
   * @returns {TextElement} This text element.
   */
  onDocument(event, listener, context) {

    const boundListener = listener.bind(context || this);
    this.document.on(event, boundListener);

    this.documentListeners[event] = this.documentListeners[event] || [];
    this.documentListeners[event].push(boundListener);

    return this;
  }

  /**
   * Unregisters listeners on document events.
   *
   * @param {string} [event] - If specified, all the listeners for that event will be unregistered. Otherwise, all listeners for all events will be unregistered.
   * @returns {TextElement} This text element.
   */
  removeDocumentListeners(event) {

    const toRemove = event ? pick(this.documentListeners, event) : this.documentListeners;
    each(toRemove, (listeners, event) => {
      each(listeners, (listener) => this.document.removeListener(event, listener));
    });

    return this;
  }

  /**
   * Called when this element's document is mutated.
   * If the mutation took place before this element in the text, this method
   * automatically moves the element's start and end indices by the correct amount.
   *
   * If the mutation concerns this element, this method does nothing
   * (it is the responsibility of elements to mutate themselves; this method
   * only reacts to the mutation of other elements).
   *
   * @param {TextMutation} mutation - The mutation that occurred.
   * @returns {undefined} Nothing.
   */
  onDocumentMutated(mutation) {
    if (mutation.element != this && this.start >= mutation.start) {
      this.moveBy(mutation.delta);
    }
  }

  /**
   * Returns a string representing this element.
   *
   * @returns {string}
   */
  toString() {
    return stringify(this, ...this.toStringProperties());
  }

  /**
   * Returns the properties that will be serialized in {@link TextElement#toString}.
   */
  toStringProperties() {
    return [ 'start', 'end', 'text' ];
  }
}

import polyfills from 'babel-polyfill';

import TextDocument from './document';
import TextElement from './element';
import ConfigurableTextElement from './element.configurable';
import TextMutation from './mutation';
import SpliceTextMutation from './mutation.splice';
import ParserBuilder from './parser-builder';
import TextRange from './range';

export { ConfigurableTextElement, ParserBuilder, SpliceTextMutation, TextDocument, TextElement, TextMutation, TextRange };

export default {
  ConfigurableTextElement: ConfigurableTextElement,
  ParserBuilder: ParserBuilder,
  SpliceTextMutation: SpliceTextMutation,
  TextDocument: TextDocument,
  TextElement: TextElement,
  TextMutation: TextMutation,
  TextRange: TextRange
};

/**
 * @external {events~EventEmitter} https://nodejs.org/api/events.html#events_class_eventemitter
 */

/**
 * A change in a text document.
 *
 * This is an abstract class that does nothing.
 * It should be subclasses and its methods overriden.
 */
export default class TextMutation {

  /**
   * Creates a new text mutation concerning the specified text element.
   *
   * @param {TextElement} element - The text element being mutated.
   */
  constructor(element) {
    // TODO: validate
    /**
     * The text element being mutated.
     * @type {TextElement}
     */
    this.element = element;
  }

  /**
   * The start index at which this mutation takes place in the text document.
   *
   * If the mutation affects an existing range of text, this must be the start index (inclusive) of that range.
   * If the mutation only adds new text, this must be the index at which that text will be inserted.
   *
   * @returns {integer} The index at which this mutation takes place in the text document.
   */
  get start() {
    return undefined;
  }

  /**
   * The end index at which this mutation takes place in the text document.
   *
   * If the mutation affects an existing range of text, this must be the end index (exclusive) of that range.
   * If the mutation only adds new text, this must be `undefined`.
   *
   * @returns {integer|undefined} The end index (exclusive) of the existing text range affected by this mutation, or `undefined` if the mutation only adds new text.
   */
  get end() {
    return undefined;
  }

  /**
   * The positive or negative difference of the total length of the text document after the mutation is applied.
   *
   * * If the mutation adds 3 characters, the delta is 3.
   * * If the mutation adds 5 characters and removes 7, the delta is -2.
   *
   * @returns {integer} The number of characters added to or removed from the document by this mutation.
   */
  get delta() {
    return 0;
  }

  /**
   * Applies this mutation to the specified text.
   *
   * @param {string} text - The text to change.
   * @returns {string} The changed text.
   */
  apply(text) {
    return text;
  }

}

import TextMutation from './mutation';

/**
 * A text mutation that removes and/or adds characters at a specific index in a text, much like `Array.prototype.splice` does for arrays.
 *
 * @see https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Array/splice
 */
export default class SpliceTextMutation extends TextMutation {

  /**
   * Creates a splice mutation.
   *
   * @param {TextElement} element - The text element being mutated.
   * @param {integer} index - The index in the text document at which to remove and/or add characters.
   * @param {integer} removeCount - How many characters to remove at the specified index (can be 0).
   * @param {string} [textToAdd] - The text to add at the specified index (can be omitted if you only need to remove text).
   */
  constructor(element, index, removeCount, textToAdd) {
    // TODO: validate
    super(element);

    /**
     * The index in the text document at which to remove and/or add characters.
     * @type {integer}
     */
    this.index = index;

    /**
     * How many characters to remove at the specified index.
     * @type {integer}
     */
    this.removeCount = removeCount;

    /**
     * The text to add at the specified index.
     * @type {string}
     */
    this.textToAdd = textToAdd;
  }

  /**
   * Returns the index at which splicing takes place in the text document.
   *
   * @returns {integer} The index at which splicing takes place.
   */
  get start() {
    return this.index;
  }

  /**
   * Returns the end index (exclusive) of the text range removed by the mutation, if any.
   *
   * @returns {integer|undefined} The end index (exclusive) of the text range removed by the mutation, or `undefined` if it only adds new text.
   */
  get end() {
    return this.removeCount ? this.index + this.removeCount : undefined;
  }

  /**
   * Returns the difference between the length of the added text and the number of removed characters.
   *
   * @returns {integer} The number of characters added to or removed from the document by this mutation.
   */
  get delta() {
    return (this.removeCount ? -this.removeCount : 0) + (this.textToAdd ? this.textToAdd.length : 0);
  }

  /**
   * Applies this mutation to the specified text.
   *
   * @param {string} text - The text to change.
   * @returns {string} The changed text.
   */
  apply(text) {
    if (this.removeCount) {
      text = text.slice(0, this.index) + text.slice(this.index + this.removeCount);
    }

    if (this.textToAdd) {
      text = text.slice(0, this.index) + this.textToAdd + text.slice(this.index);
    }

    return text;
  }

}

import { extend, pick } from 'lodash';

import ConfigurableTextElement from './element.configurable';

/**
 * A chainable builder to add a text parser to a {@link TextDocument}.
 *
 * {@link TextDocument} parsers are generator functions that must return {@link TextElement} objects, usually a subclass.
 * This builder simplifies making such a function.
 *
 * @example
 * // Adds a parser that will prepend a horizontal rule before all titles in a Markdown document (except the main title).
 * document.buildParser('MarkdownTitle').regexp(/^(#+)\s*(.+)$/gm).mutate(function(data) {
 *   const titleLevel = data.match[1].length;
 *   if (titleLevel >= 2) {
 *     this.prepend('---\n');
 *   }
 * }).add();
 */
export default class ParserBuilder {

  /**
   * Creates a builder that will add a parser to the specified document.
   *
   * @param {TextDocument} document - The document to add the parser to.
   * @param {string} [type] - An optional type that will be set on parsed {@link TextElement} objects.
   * This can be useful to distinguish between different element types since all elements parsed by this build's parser will be instances of the same {@link TextElement} subclass.
   */
  constructor(document, type) {

    /**
     * The document to which this builder will add parsers.
     * @type {TextDocument}
     */
    this.document = document;

    if (type !== undefined) {
      /**
       * The type of parsed {@link TextElement} objects.
       * @type {string}
       */
      this.type = type;
    }
  }

  /**
   * Configures the parser to look for text elements matching the specified regular expression in the document.
   *
   * The regular expression can have the global flag or not, depending on whether you need to parse a single or multiple elements.
   *
   * @param {RegExp} regexp - The regular expression to use to find text elements.
   * @returns {ParserBuilder} This builder.
   */
  regexp(regexp) {

    /**
     * @access private
     */
    this.generator = regexpGeneratorFactory(regexp, (document, match) => {
      return createElement(this, match.index, match[0], {
        match: match
      });
    });

    return this;
  }

  /**
   * Sets the type that will be set on parsed {@link TextElement} objects.
   *
   * This overrides the type supplied to the constructor.
   *
   * @param {string} type - The type to set on parsed {@link TextElement} objects.
   * @returns {ParserBuilder} This builder.
   */
  elementType(type) {
    this.type = type;
    return this;
  }

  /**
   * Sets the initializer function that will be invoked for each parsed {@link TextElement} object.
   * It will be bound to the parsed object.
   *
   * @param {function} initializer - An initializer function.
   * @returns {ParserBuilder} This builder.
   */
  initialize(initializer) {
    /**
     * @access private
     */
    this.initializer = initializer;
    return this;
  }

  /**
   * Sets the mutator function that will be invoked when each parsed {@link TextElement} object is mutated.
   * It will be bound to the parsed object.
   *
   * @param {function} mutator - A mutator function.
   * @returns {ParserBuilder} This builder.
   */
  mutate(mutator) {
    /**
     * @access private
     */
    this.mutator = mutator;
    return this;
  }

  /**
   * Adds the configured parser to this builder's document.
   *
   * @returns {ParserBuilder} This builder.
   */
  add() {
    this.document.addParser(this.generator);
    return this;
  }

}

function createElement(builder, start, text, data) {
  const element = new ConfigurableTextElement(builder.document, start, text, data);
  extend(element, pick(builder, 'initializer', 'mutator', 'type'));
  return element;
}

function regexpGeneratorFactory(regexp, factory) {
  return function*(document) {

    let match;
    if (!regexp.global) {
      if ((match = regexp.exec(document.text)) !== null) {
        yield factory(document, match);
      }

      return;
    }

    let index;
    while ((match = regexp.exec(document.text)) !== null) {
      if (match.index === index) {
        throw new Error('Parser must not match the same text twice (at index ' + index + ')');
      }

      index = match.index;
      yield factory(document, match);
    }
  };
}

